import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: 'loginView',
    component: () => import( '../views/login.vue')
  },
  {
    path: '/home',
    name: 'homeView',
    component: () => import( '../views/home.vue'),
    children: [
      {
        path: '/roles',
        name: 'rolesView',
        component: () => import( '../views/roles.vue')
      },
      {
        path: '/category',
        name: 'categoryView',
        component: () => import( '../views/category.vue')
      },
      {
        path: '/search',
        name: 'searchView',
        component: () => import( '../views/search.vue')
      },
      {
        path: '/create',
        name: 'editView',
        component: () => import( '../views/edit.vue')
      },
      {
        path: '/edit/:billno',
        name: 'editView',
        component: () => import( '../views/edit.vue')
      }
    ]
  }
]

const router = new VueRouter({
  routes
})
// 路由守卫
router.beforeEach((to, from, next) => {
  const rolesno = localStorage.getItem('rolesno')
  if (to.path === '/') {
    next()
  }else{
    if (rolesno==null) {
      next('/')
    }else{
      next()
    }
  }
})

export default router
