import "core-js/modules/es.array.push.js";
import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
// Flatpickrを導入する
import flatpickr from 'flatpickr';
import 'flatpickr/dist/flatpickr.css';
import 'flatpickr/dist/l10n/ja';
export default {
  name: 'billView',
  data() {
    return {
      drawerflag: false,
      multipleSelection: [],
      //ロジック処理
      conditionFlag: false,
      sortFlag: false,
      onSubmitFlag: false,
      //ボタンの有効化
      editBt: true,
      agreeBt: true,
      disagreeBt: true,
      reviewBt: false,
      downloadBt: false,
      editPermissonsBt: false,
      //順番処理
      sort: false,
      order: '',
      //ページング部分の機能
      totalBill: '',
      currentPage: 1,
      pagesize: 10,
      //条件付き検索部分の機能
      customers: [],
      billTypes: [],
      updateUsers: [],
      timeout: null,
      date: '',
      //帳票詳細
      bill: {
        billno: '',
        billType: '',
        customer: '',
        remark: '',
        transactionAmount: '',
        transactionymd: '',
        updateUserName: '',
        updateymd: '',
        reviewStatus: '',
        editPermissions: false,
        imageUrl: '',
        Logs: []
      },
      //帳票一覧
      billTableData: [],
      //検索条件
      condition: {
        transactDateFrom: '',
        transactDateTo: '',
        transactionAmountFrom: '',
        transactionAmountTo: '',
        customerno: '',
        billTypeno: '',
        updateUserid: ''
      }
    };
  },
  computed: {
    exportBt() {
      if (this.multipleSelection.length == 0) {
        return true;
      } else {
        return false;
      }
    }
  },
  methods: {
    handleSizeChange(size) {
      this.pagesize = size;
      this.getBillsByFlag();
    },
    //順番処理
    sortChange(column) {
      this.sort = column.order;
      this.order = column.prop;
      this.sortFlag = true;
      this.currentPage = 1;
      if (this.conditionFlag == true) {
        this.$http.post('/bill/conditionSearch', {
          currentPage: this.currentPage,
          pagesize: this.pagesize,
          condition: this.condition,
          sort: this.sort,
          order: this.order
        }).then(res => {
          if (res.data.code == 200) {
            this.billTableData = res.data.data.bills;
            this.totalBill = res.data.data[0].totalBill;
          }
        });
      } else if (this.onSubmitFlag == true) {
        this.$http.post('//bill/getOnSubmit', {
          currentPage: this.currentPage,
          pagesize: this.pagesize,
          sort: this.sort,
          order: this.order
        }).then(res => {
          if (res.data.code == 200) {
            this.billTableData = res.data.data.bills;
            this.totalBill = res.data.data[0].totalBill;
          }
        });
      } else {
        this.$http.post('/bill/getBills', {
          currentPage: this.currentPage,
          pagesize: this.pagesize,
          order: this.sort,
          prop: this.order
        }).then(res => {
          if (res.data.code == 200) {
            this.billTableData = res.data.data;
          }
        });
      }
    },
    //金額のチェック
    checkAmountFrom() {
      if (!/^-?\d+$/.test(this.condition.transactionAmountFrom) && this.condition.transactionAmountFrom != '') {
        this.$message({
          type: 'error',
          message: '整数のみ入力'
        });
        this.condition.transactionAmountFrom = '';
      }
    },
    checkAmountTo() {
      if (!/^-?\d+$/.test(this.condition.transactionAmountTo) && this.condition.transactionAmountTo != '') {
        this.$message({
          type: 'error',
          message: '整数のみ入力'
        });
        this.condition.transactionAmountTo = '';
      }
    },
    //編集権限を付くボタン
    giveEditPermissons() {
      this.$http.get('/bill/giveEditPermissons/' + this.bill.billno).then(res => {
        if (res.data.code == 200) {
          this.$message({
            type: 'success',
            message: '編集権限を付く完了'
          });
        }
      });
    },
    //編集ボタン
    edit() {
      this.$router.push({
        path: '/edit/' + this.bill.billno
      });
    },
    //承認ボタン
    agree() {
      this.$http.get('/bill/agreeBill').then(res => {
        if (res.data.code == 200) {
          this.$message({
            type: 'success',
            message: '承認完了'
          });
          this.drawerflag = false;
        }
      });
    },
    //拒否ボタン
    disagree() {
      this.$http.get('/bill/disagreeBill').then(res => {
        if (res.data.code == 200) {
          this.$message({
            type: 'success',
            message: '拒否完了'
          });
          this.drawerflag = false;
        }
      });
    },
    //ページジャンプ
    pageCurrentChange(val) {
      this.currentPage = val;
      this.getBillsByFlag();
    },
    //条件付き検索・承認待ち・ソート機能を別で帳票データを取得する
    getBillsByFlag() {
      if (this.sortFlag == false) {
        if (this.conditionFlag == true) {
          this.$http.post('/bill/conditionSearch', {
            currentPage: this.currentPage,
            pagesize: this.pagesize,
            condition: this.condition
          }).then(res => {
            if (res.data.code == 200) {
              this.billTableData = res.data.data.bills;
              this.totalBill = res.data.data[0].totalBill;
            }
          });
        } else if (this.onSubmitFlag == true) {
          this.$http.post('//bill/getOnSubmit', {
            currentPage: this.currentPage,
            pagesize: this.pagesize
          }).then(res => {
            if (res.data.code == 200) {
              this.billTableData = res.data.data.bills;
              this.totalBill = res.data.data[0].totalBill;
            }
          });
        } else {
          this.getBills();
        }
      } else {
        if (this.conditionFlag == true) {
          this.$http.post('/bill/conditionSearch', {
            currentPage: this.currentPage,
            pagesize: this.pagesize,
            condition: this.condition,
            sort: this.sort,
            order: this.order
          }).then(res => {
            if (res.data.code == 200) {
              this.billTableData = res.data.data.bills;
              this.totalBill = res.data.data[0].totalBill;
            }
          });
        } else if (this.onSubmitFlag == true) {
          this.$http.post('//bill/getOnSubmit', {
            currentPage: this.currentPage,
            pagesize: this.pagesize,
            sort: this.sort,
            order: this.order
          }).then(res => {
            if (res.data.code == 200) {
              this.billTableData = res.data.data.bills;
              this.totalBill = res.data.data[0].totalBill;
            }
          });
        } else {
          this.$http.post('/bill/getBills', {
            currentPage: this.currentPage,
            pagesize: this.pagesize,
            sort: this.sort,
            order: this.order
          }).then(res => {
            if (res.data.code == 200) {
              this.billTableData = res.data.data.bills;
              this.totalBill = res.data.data[0].totalBill;
            }
          });
        }
      }
    },
    //帳票詳細をクリアする
    clearBill() {
      this.bill = {
        billType: '',
        customer: '',
        remark: '',
        transactionAmount: '',
        transactionymd: '',
        updateUserName: '',
        updateymd: '',
        imageUrl: '',
        Logs: []
      };
    },
    //帳票データを取得する
    getBills() {
      this.$http.post('/bill/getBills', {
        currentPage: this.currentPage,
        pagesize: this.pagesize
      }).then(res => {
        if (res.data.code == 200) {
          this.billTableData = res.data.data;
          this.totalBill = res.data.data[0].totalBill;
        }
      });
    },
    //チェックボックスを選択する
    handleSelectionChange(val) {
      this.multipleSelection = [];
      val.forEach(element => {
        this.multipleSelection.push(element.billno);
      });
    },
    //検索ボタン
    search() {
      const dateFrom = new Date(this.condition.transactDateFrom);
      const dateTo = new Date(this.condition.transactDateTo);
      if (dateFrom > dateTo) {
        this.$message({
          type: 'error',
          message: '開始日は終了日より小さい'
        });
        return;
      }
      if (this.condition.transactionAmountFrom > this.condition.transactionAmountTo) {
        this.$message({
          type: 'error',
          message: '金額範囲は0より小さい'
        });
        return;
      }
      this.conditionFlag = true;
      this.onSubmitFlag = false;
      this.currentPage = 1;
      this.$http.post('/bill/conditionSearch', {
        currentPage: this.currentPage,
        pagesize: this.pagesize,
        condition: this.condition
      }).then(res => {
        if (res.data.code == 200) {
          this.billTableData = res.data.data;
          this.totalBill = res.data.data[0].totalBill;
        }
      });
    },
    //クリアボタン
    clear() {
      this.condition = {
        date: '',
        moneyMin: '',
        moneyMax: '',
        customer: '',
        billType: '',
        updateUserid: ''
      };
      this.onSubmitFlag = false;
      this.conditionFlag = false;
      this.currentPage = 1;
      this.sortFlag = false;
      this.sort = null;
      this.order = null;
      this.getBills();
    },
    //承認待ちボタン
    onSubmit() {
      this.onSubmitFlag = true;
      this.conditionFlag = false;
      this.currentPage = 1;
      this.$http.post('//bill/getOnSubmit', {
        currentPage: this.currentPage,
        pagesize: this.pagesize
      }).then(res => {
        if (res.data.code == 200) {
          this.billTableData = res.data.data.bills;
          this.totalBill = res.data.data[0].totalBill;
        }
      });
    },
    //ダウンロードボタン
    download() {
      this.$http.post('/bill/downloadBill', this.multipleSelection, {
        responseType: 'blob'
      }).then(res => {
        const blob = new Blob([res.data]);
        const url = window.URL.createObjectURL(blob);

        // 创建一个 <a> 标签用于下载
        const link = document.createElement('a');
        link.href = url;
        link.download = 'downloaded_file.txt'; // 设置下载文件的名称
        link.click();

        // 释放资源
        window.URL.revokeObjectURL(url);
      });
    },
    //当帳票の詳細を表示する
    handleRowClick(row) {
      this.$http.get('/bill/getBillDetails/' + row.billno).then(res => {
        if (res.data.code == 200) {
          for (let i = 0; i < res.data.data.length; i++) {
            if (i == 0) {
              this.bill = res.data.data[i];
            } else {
              this.bill.Logs[i] = res.data.data[i];
            }
          }
        }
      });
      //編集権限を判断する
      if (this.bill.reviewStatus == 1) {
        this.agreeBt = true;
        this.disagreeBt = true;
      }
      if (!this.bill.editPermissions) {
        if (sessionStorage.getItem("rolesno") == 4 || sessionStorage.getItem("rolesno") == 3) {
          this.editPermissonsBt = true;
        }
        this.editBt = true;
      } else {
        this.editPermissonsBt = false;
      }
      this.drawerflag = true;
    },
    //アップロードしたファイルを表示する
    handleAvatarSuccess(response) {
      console.log(response);
      this.bill.imageUrl = response;
    },
    //サーバーからデータを取得する
    loadCustomers() {
      this.$http.get('/category/getCustomers').then(res => {
        if (res.data.code == 200) {
          return res.data.data;
        }
      });
      // return [
      //     { "customer": "三全鲜食（北新泾店）", "customerno": 1 },
      //     { "customer": "Hot honey 首尔炸鸡（仙霞路）", "customerno": 2 }
      // ];
    },

    loadBillTypes() {
      this.$http.get('/category/getBillTypes').then(res => {
        if (res.data.code == 200) {
          return res.data.data;
        }
      });
      // return [
      //     { "billType": "胖仙女纸杯蛋糕（上海凌空店）", "billTypeno": "上海市长宁区金钟路968号1幢18号楼一层商铺18-101" },
      //     { "billType": "贡茶", "billTypeno": "上海市长宁区金钟路633号" },
      // ];
    },

    loadUpdateUsers() {
      this.$http.get('/bill/getUpdateUserids').then(res => {
        if (res.data.code == 200) {
          return res.data.data;
        }
      });
      // return [
      //     { "updateUserName": "茶芝兰（奶茶，手抓饼）", "updateUserid": "上海市普陀区同普路1435号" },
      //     { "updateUserName": "十二泷町", "updateUserid": "上海市北翟路1444弄81号B幢-107" },
      // ];
    }
  },

  //画面がロードされたら、取引先などのデータを取得する
  mounted() {
    this.customers = this.loadCustomers();
    this.billTypes = this.loadBillTypes();
    this.updateUsers = this.loadUpdateUsers();
    this.getBills();
    var rolesno = sessionStorage.getItem("rolesno");
    switch (rolesno) {
      case '1':
        this.reviewBt = false;
        this.downloadBt = false;
        this.editBt = false;
        break;
      case '2':
        this.reviewBt = false;
        this.downloadBt = true;
        break;
      case '3':
        this.reviewBt = true;
        this.downloadBt = true;
        this.agreeBt = false;
        this.disagreeBt = false;
        break;
      case "4":
        this.editFlag = true;
        this.reviewBt = true;
        this.downloadBt = true;
        this.editBt = false;
        this.agreeBt = false;
        this.disagreeBt = false;
        this.editPermissonsBt = true;
        break;
    }

    //日付の設定
    flatpickr(this.$refs.datepickerInput1, {
      dateFormat: 'Y/m/d',
      locale: 'ja',
      firstDayOfWeek: '0',
      allowInput: 'true',
      minDate: "1990/01/01",
      maxDate: "today"
    });
    flatpickr(this.$refs.datepickerInput2, {
      dateFormat: 'Y/m/d',
      locale: 'ja',
      firstDayOfWeek: '0',
      allowInput: 'true',
      minDate: "1990/01/01",
      maxDate: "today"
    });
  }
};
